if (this.view.lblStatus.text === "Active") {
  // Active label copy, not showing "activate" icon
  this.view.flxImgActivate.setVisibility(false);
  if (context.eventType !== "leave"){
    this.view.flxShowIcons.setVisibility(true);
  } else {
    this.view.flxShowIcons.setVisibility(false);
  }

} else {
  // Not active label copy, showing "activate" icon
  if (context.eventType !== "leave"){
    this.view.flxShowIcons.setVisibility(true);
  } else {
    this.view.flxShowIcons.setVisibility(false);
  }

}
