// gblLabelsPage not empty
if(gblLabelsPage.length !== 0) {

  var x = gblLabelsPage - 1; // in order to make the formula work
  // taking only 5 keys at a time

  // Copying the list but removing the "id" key
  list = gblLabelsList.map(function(obj) {
    // Crea una copia dell'oggetto corrente
    var newObj = Object.assign({}, obj);
    // Rimuovi la chiave "id" dall'oggetto corrente
    delete newObj.id;
    return newObj;
  });

  var firstKeys = Object.keys(list[0]).slice( (5*x) , (5*gblLabelsPage) );

  this.view.segLabels.widgetDataMap = {lb1: firstKeys[0],lb2: firstKeys[1],lb3: firstKeys[2],lb4: firstKeys[3], lb5: firstKeys[4]};

  // in case the user wants all the labels
  var data = list;  
  // checking for the selected number of labels to show
  if (this.view.lbShowEntries.selectedKeyValue[1] !== "all") {

    var showLabels = parseInt(this.view.lbShowEntries.selectedKeyValue[1], 10);  // 10 is for the decimal
    //when labels file is uploaded one row is used just to map the header
    data = list.slice(0, (gblLabelsFileUploaded ? ++showLabels : showLabels) );

  }

  this.view.segLabels.setData(data);
}
