debugger;

// removing flxScroll related to the step we are deleting
let scroll = this.findCurrentFlexScroll();
this.view.flxRightSide.remove(scroll);

// removing the step box
let stepBoxIdToDelete = "boxStep" + gblCurrentStepOrder;
let widget = this.view.flxSteps.widgets().find(w => w.id === stepBoxIdToDelete);
this.view.flxSteps.remove(widget);

// decreasing gbl variables only if different from first.
gblCurrentStepOrder = gblCurrentStepOrder === 1 ? 1 : --gblCurrentStepOrder;
gblLastInsertedStep = gblLastInsertedStep === 1 ? 1 : --gblLastInsertedStep;


this.highlightSelectedStepBox();
let flxScrollToShow = this.findCurrentFlexScroll();
flxScrollToShow.setVisibility(true);

this.view.flxPopupDeleteStep.setVisibility(false);
this.view.flxNewStepBlur.setVisibility(false);


