const keys = Object.keys(self.modes);
keys.sort();  // Ordina le chiavi per garantire l'ordine corretto

for (const key of keys) {
  const match = key.match(/(.*)_(\d+)$/);
  if (match) {
    const prefix = match[1];
    const number = parseInt(match[2]);
    if (number > 2) {
      const newKey = prefix + "_" + (parseInt(number)-1).toString();
      //const newKey = `${prefix}_${number - 1}`;
      self.modes[newKey] = self.modes[key];
      delete self.modes[key];
    }
  }
