// retrieving all step's boxes
let steps_widgets = self.view.flxSteps.widgets();

voltmx.print("### FLEX STEPS NUMBER OF WIDGETS: " + JSON.stringify(steps_widgets.length));

// many steps already existing
if (steps_widgets.length > 2){
//   debugger;
  let flxScrolls = this.view.flxRightSide.widgets();

  // for each step box existing, I set them non-focused (white background and no icons shown)
  for (let stepNumber = 2; stepNumber <= gblLastInsertedStep; stepNumber++) {
    let currentStepId = 'boxStep' + stepNumber;
    let currentScrollId = 'flxScrollRight' + stepNumber;
    let widget = steps_widgets.find(w => w.id === currentStepId);
    if (widget) {
      widget.flxBoxStep.backgroundColor = "FFFFFF00";
      widget.imgDeleteStep.isVisible = false;
      widget.imgEditStep.isVisible = false;
      widget.lblStepOrder.fontColor = "00000000";
      widget.lblStepTitle.fontColor = "00000000";   
    }
    // showing only scroll related to the first step
    let scroll = flxScrolls.find(s => s.id === currentScrollId);
    if (scroll) {
      scroll.isVisible = false;
    }
  }
  // setting the first step's box on focus (black background, icons shown, related scroll shown)
  this.view.flxBoxFirstStep.backgroundColor = "00000000";
  this.view.flxBoxFirstStep.imgDeleteStep.isVisible = true;
  this.view.flxBoxFirstStep.imgEditStep.isVisible = true;
  this.view.flxBoxFirstStep.lblStepOrder.fontColor = "FFFFFF00";
  this.view.flxBoxFirstStep.lblStepTitleIntoStepComposition.fontColor = "FFFFFF00";
  this.view.flxRightSide.flxScrollRight.setVisibility(true);
  this.view.settingsSide.flxScrollSettingsContent.removeAll();
  this.view.settingsSide.flxScrollSettingsContent.setVisibility(false);
  this.view.settingsSide.txt.setVisibility(true);
  gblCurrentStepOrder = 1;
  voltmx.print("### CURRENT STEP ORDER: " + JSON.stringify(gblCurrentStepOrder));
}
