let content_right = self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.widgets();
let targetWidget = null; // boolean

// if the current Step is different from 1, we need to find the content related to the step we are in currently
if (gblCurrentStepOrder > 1){
  targetWidget = content_right.find(widget => widget.id === "flxScrollRight" + gblCurrentStepOrder);
} else {
  targetWidget = self.view.flxRightSide.flxScrollRight;  
}
// debugger;

// we found some components previously selected for this current Step. We put them visible.
if (targetWidget){
  let content_selected = targetWidget.widgets();
  if (content_selected.length > 0){
    voltmx.print("### THERE ARE SOME SELECTED COMPONENTS!");
    content_selected.forEach(function(widg) {
      voltmx.print("### WIDGET ID: " + JSON.stringify(widg.id));
      //    debugger; 
      if (widg.id && widg.id.includes("flex")) {
        voltmx.print("### SELECTED COMPONENT FOUND");
        widg.isVisible = true;
        //   	debugger; // Questo fermerà l'esecuzione qui permettendoti di ispezionare `widget`
      }
    });
    targetWidget.forceLayout();

  } else { // no components previously selected for the current Step, we display the standard message
    self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.imgNoComponentsRight.setVisibility(true);
    self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.lblNoComponentsRight.setVisibility(true);
    self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.txtStartPhraseRight.setVisibility(true);
  }
}
self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.setEnabled(true);

