// now hiding the selected components' arrows, clone/delete box and "add nested button"
let scroll_widgets = self.view.flxLeftSide.flxScrollLeft.widgets();
voltmx.print("### scroll_widgets: -" + (scroll_widgets) + "-");
//voltmx.print("### scroll_widgets: -" + JSON.stringify(scroll_widgets) + "-");

scroll_widgets.forEach( widg => {
  let widget_key = Object.keys(widg).find(key => key.startsWith("component"));
  let chiavi = Object.keys(widg);
  voltmx.print("### chiavi: -" + chiavi + "-");
  voltmx.print("### widget_key: -" + widget_key + "-");

  widg[widget_key].arrowUpVisible = false;
  widg[widget_key].arrowDownVisible = false;
  widg[widget_key].flxMoveVisible = false;
  widg[widget_key].cloneDeleteVisible = false;
  widg[widget_key].flxAddNestedVisible = false;
});

voltmx.print("### FINITO A SINISTRA");





self.view.ComponentsSide.segComponentsSideConfigurableContent.setData(gblConfigurableComponentsTemplates);
self.view.ComponentsSide.segComponentsSideSimpleContent.setData(gblSimpleComponentsTemplates);

self.view.ComponentsSide.segComponentsSideConfigurableContent.setEnabled(false);
self.view.ComponentsSide.segComponentsSideSimpleContent.setEnabled(false);

// To avoid conficts by unintentional double clicking on sides while they have the animation going on
self.view.flxRightSide.setEnabled(true);
self.view.flxLeftSide.setEnabled(true);

let steps_widgets = self.view.flxSteps.widgets();
let left_position = 0;
voltmx.print("### FLEX STEPS NUMBER OF WIDGETS: " + JSON.stringify(steps_widgets.length));

if (steps_widgets.length <= 2){
  left_position = parseInt(self.view.flxBoxPlus.left, 10) + parseInt(self.view.flxBoxPlus.width, 10) + 2;
} else {
  let lastStep = 'boxStep' + gblLastInsertedStep;
  steps_widgets.forEach(widget => {
    if (widget.id === lastStep) {
      left_position = parseInt(widget.left, 10) + parseInt(widget.width, 10) + 2;
    }
  });
}

let left_position_percentage = left_position + "%";

var animationDef = {
  "100": {
    "left": left_position_percentage
  }
};

let animDef = voltmx.ui.createAnimation(animationDef);

self.view.flxBoxFirstStep.animate(animDef, {
  "duration": 0.25,
  "iterationCount": 1,
  "delay": 0,
  "fillMode": voltmx.anim.FILL_MODE_FORWARDS
});


// now hiding the selected components' arrows, clone/delete box and "add nested button"

let scroll_widgets = self.view.flxLeftSide.flxScrollLeft.widgets();
voltmx.print("### scroll_widgets: -" + (scroll_widgets) + "-");
//voltmx.print("### scroll_widgets: -" + JSON.stringify(scroll_widgets) + "-");

scroll_widgets.forEach( widg => {
  let widget_key = Object.keys(widg).find(key => key.startsWith("component"));
  let chiavi = Object.keys(widg);
  voltmx.print("### chiavi: -" + chiavi + "-");
  voltmx.print("### widget_key: -" + widget_key + "-");

  widg[widget_key].arrowUpVisible = false;
  widg[widget_key].arrowDownVisible = false;
  widg[widget_key].flxMoveVisible = false;
  widg[widget_key].cloneDeleteVisible = false;
  widg[widget_key].flxAddNestedVisible = false;
});

voltmx.print("### FINITO A SINISTRA");

// The sides was in halves and the now the user wants to OPEN the LEFT one
// We will expand the selected side (LEFT SIDE)
voltmx.print("### RIGHT WIDTH: " + self.view.flxRightSide.width);

this.view.flxComponentsSide.opacity = 1;
this.view.flxLeftSide.skin = "HalfSelectedSide";
this.view.flxSteps.setVisibility(false);

self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.imgNoComponentsRight.setVisibility(false);
self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.lblNoComponentsRight.setVisibility(false);
self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.txtStartPhraseRight.setVisibility(false);
// self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxLeftSide.flxSelectedComponent.setVisibility(false);

// retrieving the content of the RIGHT side (in order to hide it when the LEFT SIDE is open)
var content_right = self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.flxScrollRight.widgets();

voltmx.print("### TYPE OF LEFT CONTENT: " + typeof content_right);

// for each element in the RIGHT SIDE, we hide it
content_right.forEach(function(widget) {
   voltmx.print("### WIDGET ID: " + JSON.stringify(widget.id));
//    debugger; // Questo fermerà l'esecuzione qui permettendoti di ispezionare `widget`
  if (widget.id && widget.id.includes("flex")) {
  	voltmx.print("### SELECTED COMPONENT");
    widget.isVisible = false;
//   	debugger; // Questo fermerà l'esecuzione qui permettendoti di ispezionare `widget`
  }
});


// disabling the use of the closed side (RIGHT SIDE)
self.view.flxMainContainerStepComposition.flxContainerStepCreation.flxLeftRight.flxRightSide.setEnabled(false);

//moving the step boxes from the top right to the 150% right. (hiding them)

let steps_widgets = self.view.flxSteps.widgets();

var animationDef = {
  "100": {
    "left": "150%"
  }
};

let animDef = voltmx.ui.createAnimation(animationDef);

steps_widgets.forEach((widget, index) => {
  let animConfig = {
    "duration": 0.25,
    "iterationCount": 1,
    "delay": 0.25,
    "fillMode": voltmx.anim.FILL_MODE_FORWARDS
  };
  widget.animate(animDef, animConfig);
});

// Filtering the components to keep only the non-"stepSection" ones.
// So we are keeping only the "previewSection" ones (that we need for the LEFT SIDE)

let filteredSimpleComponents = gblSimpleComponentsTemplates.filter(function(item) {
  return item.lblLayout !== "stepSection";
});


let filteredConfigurableComponents = gblConfigurableComponentsTemplates.filter(function(item) {
  return item.lblLayout !== "stepSection";
});

self.view.ComponentsSide.segComponentsSideSimpleContent.setData(filteredSimpleComponents);
self.view.ComponentsSide.segComponentsSideConfigurableContent.setData(filteredConfigurableComponents);

self.view.ComponentsSide.segComponentsSideConfigurableContent.setEnabled(true);
self.view.ComponentsSide.segComponentsSideSimpleContent.setEnabled(true);

// To avoid conficts by unintentional double clicking on sides while they have the animation going on
self.view.flxRightSide.setEnabled(true);
self.view.flxLeftSide.setEnabled(true);
