for (let i = 2; i < steps_widgets.length; i++) {
  let widget = steps_widgets[i];
  if (widget.id === "boxStep2"){
    left_position = parseInt(self.view.flxBoxFirstStep.left, 10) + parseInt(self.view.flxBoxFirstStep.width, 10) + 2;
    left_position_percentage = left_position + "%";

  } else {
    let previous_widget = steps_widgets[i-1];
    left_position = parseInt(previous_widget.left, 10) + parseInt(previous_widget.width, 10) + 2;
    left_position_percentage = left_position + "%";
  }
  let animationDef = {
    "0": {
      "left": widget.left
    },
    "100": {
      "left": left_position_percentage
    }
  };
  let animDef = voltmx.ui.createAnimation(animationDef);
  widget.animate(animDef, {
    "duration": 0.5,
    "iterationCount": 1,
    "delay": 0,
    "fillMode": voltmx.anim.FILL_MODE_FORWARDS
  });
}  

