voltmx.print("### records CustomQueryGet: " + LABEL_CustomQueryGet.records);

voltmx.print("### record CustomQueryGet stringify: " + JSON.stringify(LABEL_CustomQueryGet.records) );

// I set the data in order, putting the flow_id as first key
var list = LABEL_CustomQueryGet.records.map(elemento => {
  var newElement = { flow_id: elemento.flow_id }; // adding glow_id and value
  //var newElement2 = { id: elemento.id };
  var idValue = elemento.id; // Memorizza il valore della chiave "id"
  newElement.id = idValue; // adding key "id" and value

  for (var key in elemento) {
    if (key !== "flow_id" && key !=="id") {
      newElement[key] = elemento[key];
    }
  }
  return newElement;
});

voltmx.print("### list: " + JSON.stringify(list) );

gblLabelsList = list;

voltmx.print("### gblLabelsList: " + JSON.stringify(gblLabelsList) );
// New empty object for the header row
let headerRow = {}; 
// Saving header keys and their own values
Object.keys(gblLabelsList[0]).forEach(function(key) {
  headerRow[key] = key;
});
gblLabelsList.unshift(headerRow);

voltmx.print("### gblLabelsList dopo unshift: " + JSON.stringify(gblLabelsList) );


// Copying the list but removing the "id" key
list = gblLabelsList.map(function(obj) {
  // Crea una copia dell'oggetto corrente
  var newObj = Object.assign({}, obj);
  // Rimuovi la chiave "id" dall'oggetto corrente
  delete newObj.id;
  return newObj;
});


// saving the number of labels' columns (in order to know how many pages we have to show)
list.forEach(obj => {
  const numColumns = Object.keys(obj).length;
  if (numColumns > gblLabelsColumns) {
    gblLabelsColumns = numColumns;
  }
});

self.view.btnPrevious.setEnabled(false);
self.view.btnPrevious.opacity = 0.5;

var maxPages = Math.ceil(gblLabelsColumns / 5); // this method rounds up to the nearest integer

if (list === maxPages) { // only one page needed (the document only have 5 columns)
  self.view.btnNext.setEnabled(false);
  self.view.btnNext.opacity = 0.5;
}


// setting the first 5 columns to display
var firstKeys = Object.keys(list[0]).slice(0, 5);
voltmx.print("### firstKey: " + JSON.stringify(firstKeys) );

self.view.segLabels.widgetDataMap = {lb1: firstKeys[0],lb2: firstKeys[1],lb3: firstKeys[2],lb4: firstKeys[3], lb5: firstKeys[4]};

var showLabels = parseInt(self.view.lbShowEntries.selectedKeyValue[1], 10);  // 10 is for the decimal
var data = list.slice(0, showLabels); 

self.view.segLabels.setData(data);

voltmx.print("### gblLabelsList con header e id visibile: " + JSON.stringify(gblLabelsList) );
voltmx.print("### list con header ma senza id visibile: " + JSON.stringify(list) );
