var labelCopy = this.view.segLabels.selectedRowItems[0];

voltmx.print("### labelCopy: " + JSON.stringify(labelCopy) );

voltmx.print("### labelCopy.id: " + JSON.stringify(labelCopy.id) );

labelCopyId = labelCopy.id;

gblLabelCopyId = labelCopy.id;
gblLabelCopyFlowId = labelCopy.flow_id;
gblLabelCopyFlowName = labelCopy.flow_name; // full Flow name Ex. RB_EYE_00001
gblLabelCopyEnvironment = labelCopy.environment;
gblLabelCopyStatus = labelCopy.status;

// if con varie icone, apertura popup






if (gblDuplicateIcon) {
  // Titles
  this.view.lblDuplicateFlow.text = "Duplicate Flow - " + gblFlowName;
  this.view.lblDuplicateFlow2.text = "Duplicate Flow - " + gblFlowName;
  // Channel
  this.view.lbSelectChannel.masterData = [ ["lb1", "Select the channel in which duplicate the flow"], ["lb2", gblChannelName] ];
  this.view.lbSelectChannel.selectedKey = "lb1";

  this.view.flxPopupDuplicateFlowLarge.flxContainerFlowDuplication.flxDuplicationFlow.txtFlowDescription.text = flow.description;
  //this.view.flxPopupDuplicateFlowLarge.flxContainerFlowDuplication.flxDuplicationFlow.swIsDefaultFlow.selectedIndex = (flow.default_flag === "true" ? 0 : 1) ;
  this.view.flxPopupDuplicateFlowLarge.flxContainerFlowDuplication.flxDuplicationFlow.swIsDefaultFlow1.state = (flow.default_flag === "true" ? true : false) ;
  //this.view.flxPopupDuplicateFlowLarge.flxContainerFlowDuplication.flxDuplicationFlow.swIsSupernova.selectedIndex = (flow.supernova === "true" ? 0 : 1) ;
  this.view.flxPopupDuplicateFlowLarge.flxContainerFlowDuplication.flxDuplicationFlow.swIsSupernova1.state = (flow.supernova === "true" ? true : false) ;
  voltmx.print("### Flow description: " + flow.description);
  this.view.flxBlur.setVisibility(true);
  this.view.flxPopupDuplicateFlowSmall.setVisibility(true);
}


if (gblEditIcon) {
  // Title
  this.view.lblEditFlow.text = "Edit Flow - " + gblFlowName;
  // Channel
  this.view.lbChannelEdit.masterData = [ ["lb1", gblChannelName] ];
  this.view.lbChannelEdit.selectedKey = "lb1";
  this.view.lbChannelEdit.setEnabled(false);
  this.view.lbChannelEdit.opacity = 0.5;
  this.view.lblChannelEdit.opacity = 0.5;
  // Flow name
  this.view.txtFlowNameEdit.text = flow.id;  // !! POTREBBE SERVIRE PATH INTERO!!!
  this.view.txtFlowNameEdit.setEnabled(false);
  this.view.txtFlowNameEdit.opacity = 0.5;
  this.view.lblFlowNameEdit.opacity = 0.5;
  // Flow description
  this.view.txtFlowDescription.text = flow.description;  // !! POTREBBE SERVIRE PATH INTERO!!!
  // Flow category
  this.view.lbCategoryEdit.masterData = [ ["lb1", flow.category] ];
  this.view.lbCategoryEdit.selectedKey = "lb1";
  this.view.lbCategoryEdit.setEnabled(false);
  this.view.lbCategoryEdit.opacity = 0.5;
  this.view.lblCategoryEdit.opacity = 0.5;
  // Flow environment
  //this.view.flxContainerFlowEdit.flxEditFlow.lbCategoryEdit;

  // Is Default
  //this.view.swIsDefaultFlowEdit.selectedIndex = (flow.default_flag === "true" ? 0 : 1) ;
  this.view.swIsDefaultFlowEdit1.state = (flow.default_flag === "true" ? true : false) ;

  // Is Supernova
  //this.view.swIsSupernovaEdit.selectedIndex = (flow.supernova === "true" ? 0 : 1) ;
  this.view.swIsSupernovaEdit1.state = (flow.supernova === "true" ? true : false) ;

  // Show frame only
  this.view.swShowFrameOnly1.state = (flow.show_frame_only === "true" ? true : false) ;

  // Show frame plus lenses
  this.view.swShowFramePlusLenses1.state = (flow.show_frame_plus_lenses === "true" ? true : false) ;

  // Show savings label
  this.view.swShowSavingsLabel1.state = (flow.show_savings_label === "true" ? true : false) ;

  // Activate distance and reading
  this.view.swActivateDistanceAndReading1.state = (flow.activate_distance_and_reading === "true" ? true : false) ;


  this.view.flxBlur.setVisibility(true);
  this.view.flxPopupEditFlow.setVisibility(true);
}


if (gblArchiveIcon) {

  this.view.lblFlowInfo.text = gblFlowName;

  this.view.flxBlur.setVisibility(true);
  this.view.flxPopupArchiveFlow.setVisibility(true);

}


if (gblActivateIcon) {

  this.view.lblActivateFlow.text = "Activate Flow - " + gblFlowName;

  this.view.flxBlur.setVisibility(true);
  this.view.flxPopupActivateFlow.setVisibility(true);

}

// open duplicate popup.
this.view.flxBlur.setVisibility(true);
this.view.flxPopupDuplicateLabelCopy.setVisibility(true);

gblDuplicateLabelCopy = false;

// open publish popup.
this.view.lblTitlePublishLabelCopy.text = "Publish Label Copy n. " + gblLabelCopyId;
this.view.flxBlur.setVisibility(true);
this.view.flxPopupPublishLabelCopy.setVisibility(true);

gblPublishLabelCopy = false;

// open activate popup.
this.view.flxBlur.setVisibility(true);
this.view.flxPopupActivateLabelCopy.setVisibility(true);

